/**
 * Function to read a point configuration.
 */
void
config_point_read (ConfigPoint * w,     ///< Point configuration structure.
                   FILE * file) ///< File.
{
  double x[3];
  unsigned int i;
  fscanf (file, "%lf%lf%lf", x, x + 1, x + 2);
  for (i = 0; i < 3; ++i)
    gtk_spin_button_set_value (w->spin[i], x[i]);
}

/**
 * Function to write a point configuration.
 */
void
config_point_write (ConfigPoint * w,    ///< Point configuration structure.
                    FILE * file)        ///< File.
{
  double x[3];
  unsigned int i;
  for (i = 0; i < 3; ++i)
    x[i] = gtk_spin_button_get_value (w->spin[i]);
  fprintf (file, "%g %g %g\n", x[0], x[1], x[2]);
}

/**
 * Function to open a point configuration.
 */
static void
config_point_new (ConfigPoint * w,      ///< Point configuration structure.
                  const char *label)    ///< Label string.
{
  unsigned int i;
  w->button = (GtkButton *) gtk_button_new_with_label (label);
  gtk_widget_set_can_focus (GTK_WIDGET (w->button), FALSE);
  for (i = 0; i < 2; ++i)
    w->spin[i] = (GtkSpinButton *) gtk_spin_button_new_with_range
      (-1e6, 1e6, 0.01);
  w->spin[2] = (GtkSpinButton *) gtk_spin_button_new_with_range
    (-1e4, 1e4, 0.001);
}

/**
 * Function to open a geometry configuration.
 */
void
config_geometry_new (ConfigGeometry * w)
                     ///< Geometry configuration structure.
{
  const char *label_id[4] = { "1", "2", "3", "4" };
  const char *label_point[4] = { _("Point"), "x (m)", "y (m)", "z (m)" };
  GdkPixbuf *pixbuf;
  char *buffer;
  unsigned int i, j;
#if DEBUG_CONFIG_GEOMETRY_NEW
  printf ("config_geometry_new: start\n");
#endif
  for (i = 0; i < 4; ++i)
    config_point_new (w->point + i, label_id[i]);
  w->grid = (GtkGrid *) gtk_grid_new ();
  buffer = setlocale (LC_CTYPE, NULL);
  if (!strncmp (buffer, "es", 2) || !strncmp (buffer, "Sp", 2))
    pixbuf = gdk_pixbuf_new_from_xpm_data (diagram_geometry_es);
  else if (!strncmp (buffer, "fr", 2) || !strncmp (buffer, "Fr", 2))
    pixbuf = gdk_pixbuf_new_from_xpm_data (diagram_geometry_fr);
  else if (!strncmp (buffer, "it", 2) || !strncmp (buffer, "It", 2))
    pixbuf = gdk_pixbuf_new_from_xpm_data (diagram_geometry_it);
  else
    pixbuf = gdk_pixbuf_new_from_xpm_data (diagram_geometry_en);
  w->picture = (GtkPicture *) gtk_picture_new_for_pixbuf (pixbuf);
  gtk_grid_attach (w->grid, GTK_WIDGET (w->picture), 0, 0, 4, 1);
  for (i = 0; i < 4; ++i)
    {
      w->button[i] = (GtkButton *) gtk_button_new_with_label (label_point[i]);
      gtk_widget_set_can_focus (GTK_WIDGET (w->button[i]), FALSE);
      gtk_grid_attach (w->grid, GTK_WIDGET (w->button[i]), i, 1, 1, 1);
    }
  for (j = 0; j < 4; ++j)
    {
      gtk_grid_attach (w->grid, GTK_WIDGET (w->point[j].button),
                       0, j + 2, 1, 1);
      for (i = 0; i < 3; ++i)
        gtk_grid_attach (w->grid, GTK_WIDGET (w->point[j].spin[i]),
                         i + 1, j + 2, 1, 1);
    }
#if DEBUG_CONFIG_GEOMETRY_NEW
  printf ("config_geometry_new: end\n");
#endif
}
