/**
 * Function to show field corner point characteristics.
 */
static void
text_corner (GtkTextBuffer * text,
///< GtkTextBuffer text to write the field corner point characteristics.
             GtkTextIter * iter,
///< GtkTextIter to write the field corner point characteristics.
             unsigned int corner,       ///< Field corner point number.
             double x,          ///< Field corner point x-coordinate.
             double y,          ///< Field corner point y-coordinate.
             double z)          ///< Field corner point z-coordinate.
{
  char buffer[512];
  snprintf (buffer, 512, "\t%s %u\n", _("Coordinates of corner"), corner);
  gtk_text_buffer_insert_with_tags_by_name (text, iter, buffer, -1,
                                            "bold", NULL);
  snprintf (buffer, 512, "\t\tx = %g m\n", x);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\ty = %g m\n", y);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\tz = %g m\n", z);
  gtk_text_buffer_insert (text, iter, buffer, -1);
}

/**
 * Function to show furrow characteristics.
 */
static void
text_furrow (GtkTextBuffer * text,
///< GtkTextBuffer text to write the furrow characteristics.
             GtkTextIter * iter,
///< GtkTextIter to write the furrow characteristics.
             const char *label, ///< Furrow label.
             Furrow * furrow)   ///< Furrow structure.
{
  char buffer[512];
  snprintf (buffer, 512, "\t%s\n", label);
  gtk_text_buffer_insert_with_tags_by_name (text, iter, buffer, -1,
                                            "bold", NULL);
  snprintf (buffer, 512, "\t\t%s = " FPF " m\n", _("Base width"), furrow->b);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPF "\n", _("Lateral slope"), furrow->z);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPF " m\n", _("Depth"), furrow->h);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPF " m\n", _("Distance"), furrow->D);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPF " m\n",
            _("Soil retention capacity"), furrow->H);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  if (furrow->epsilon > 0.)
    {
      snprintf (buffer, 512, "\t\t%s = %s\n", _("Friction model"), "Burguete");
      gtk_text_buffer_insert (text, iter, buffer, -1);
      snprintf (buffer, 512, "\t\t%s = " FPF "\n",
                _("Aerodynamical coefficient"), furrow->epsilon);
      gtk_text_buffer_insert (text, iter, buffer, -1);
      snprintf (buffer, 512, "\t\t%s = " FPF " m\n",
                _("Characteristic roughness length"), furrow->n);
      gtk_text_buffer_insert (text, iter, buffer, -1);
    }
  else
    {
      snprintf (buffer, 512, "\t\t%s = %s\n", _("Friction model"),
                "Gauckler-Manning");
      gtk_text_buffer_insert (text, iter, buffer, -1);
      snprintf (buffer, 512, "\t\t%s = " FPF " sm",
                _("Number of Gauckler-Manning"), furrow->n);
      gtk_text_buffer_insert (text, iter, buffer, -1);
      gtk_text_buffer_insert_with_tags_by_name (text, iter, "-1/3", -1,
                                                "superscript", NULL);
      gtk_text_buffer_insert (text, iter, "\n", -1);
    }
  snprintf (buffer, 512, "\t\t%s = " FPF " ms",
            _("Kostiakov's coefficient"), furrow->i1);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "-" FPF "", furrow->i2);
  gtk_text_buffer_insert_with_tags_by_name (text, iter, buffer, -1,
                                            "superscript", NULL);
  gtk_text_buffer_insert (text, iter, "\n", -1);
  snprintf (buffer, 512, "\t\t%s = " FPF "\n", _("Kostiakov's exponent"),
            furrow->i2);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPF " m/s\n",
            _("Infiltration velocity in saturated soil"), furrow->i3);
  gtk_text_buffer_insert (text, iter, buffer, -1);
}

/**
 * Function to show input irrigation characteristics.
 */
static void
text_input (GtkTextBuffer * text,
///< GtkTextBuffer text to write the input irrigation characteristics.
            GtkTextIter * iter,
///< GtkTextIter to write the input irrigation characteristics.
            unsigned int type,  ///< Input irrigation type.
            unsigned int i,     ///< Input irrigation number.
            Input * input)      ///< Input irrigation structure.
{
  char buffer[512];
  char *label;
  if (type)
    label = _("Fertilizer input");
  else
    label = _("Water input");
  snprintf (buffer, 512, "\t%s %u\n", label, i);
  gtk_text_buffer_insert_with_tags_by_name (text, iter, buffer, -1,
                                            "bold", NULL);
  snprintf (buffer, 512, "\t\tx = " FPL " m\n", input->x);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\ty = " FPL " m\n", input->y);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPL " s\n", _("Initial time"), input->t0);
  gtk_text_buffer_insert (text, iter, buffer, -1);
  snprintf (buffer, 512, "\t\t%s = " FPL " s\n", _("Final time"), input->tf);
  if (type)
    snprintf (buffer, 512, "\t\t%s = " FPL " kg/s\n", _("Discharge"), input->q);
  else
    snprintf (buffer, 512, "\t\t%s = " FPL " m\302\263/s\n",
              _("Discharge"), input->q);
  gtk_text_buffer_insert (text, iter, buffer, -1);
}

